// 如果启用调试，定义调试输出宏
#if DEBUG
    #define DEBUG_PRINT(fmt, ...) printf(fmt, __VA_ARGS__)
#else
    #define DEBUG_PRINT(fmt, ...) // 如果不启用调试，什么都不做
#endif

void print_hex(const char *data, int len) {
    for (int i = 0; i < len; i++) {
        printf("%02x ", (unsigned char)data[i]);
    }
    printf("\n");
}

// 保存结果到CSV文件
void save_results_to_csv(long throughput, double packet_loss, double avg_latency, long min_latency, long max_latency, int sent_count, int packet_size) {
    FILE *fp = fopen("results.csv", "a");
    if (fp == NULL) {
        perror("打开文件失败");
        exit(EXIT_FAILURE);
    }

    fprintf(fp, "%ld, %.2f, %.2f, %ld, %ld, %d, %d\n", throughput, packet_loss, avg_latency, min_latency, max_latency, sent_count, packet_size);
    fclose(fp);
}

